INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T10', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%ED%8B%88%EC%83%88%EC%97%AC%ED%96%89%2F10.%E1%84%8B%E1%85%A7%E1%84%89%E1%85%AE%20%E1%84%82%E1%85%A1%E1%86%BC%E1%84%86%E1%85%A1%E1%86%AB%E1%84%91%E1%85%A9%E1%84%8E%E1%85%A1.webp?alt=media&token=c0240ab5-0f0b-4811-ab28-dd46dbf83b2c ', '얼마 전에 여수 1박2일 여행 코스로 다녀왔는데요.날씨가 너무 춥다 보니 실내 여행지 위주로 다녀왔답니다.날씨가 추워서 여수 실내 여행지 위주로 다녀오긴 했지만그래도 나름 알차게 여행을 하고 왔는데요.오늘은 여수 1박2일 여행 코스와 루트까지 소개해드릴 테니 주목해 주세요~!!숙소에서 시간을 오래 보냈기 때문에 이렇게 조트 곳곳 찍기 좋았답니다.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T11', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EA%B2%A8%EC%9A%B8%EC%97%AC%ED%96%89%2F11%20%E1%84%8B%E1%85%AF%E1%86%AF%E1%84%8C%E1%85%A5%E1%86%BC%E1%84%89%E1%85%A1%E1%86%AB.jpeg?alt=media&token=e1998d90-a6f4-4055-9ded-f58343d5003a ', '1. 해상 케이블카여수 1박2일 여행 코스 중2일 차에 아침 먹고 갔던 곳이랍니다.돌산공원과 바로 이어져 있기 때문에지난날 밤에 갔으면 시간 단축도 되고밤바다를감상할 수 있지만낮에 확 트인 뷰를 감상하고 싶어서다음날 낮에 다시 방했어요.밤도 운치 있지만이렇게 낮에 파란 하늘과 바다를감상하는 게 더 힐링 되고 기분데요.층별로 다른 느낌의 인테리어를 감상할 수 있으니 구경 해보시면서 사진도 많이 찍어보세요^_^', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T12', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EA%B2%A8%EC%9A%B8%EC%97%AC%ED%96%89%2F12.%E1%84%8B%E1%85%A7%E1%84%89%E1%85%AE%20%E1%84%8F%E1%85%A6%E1%84%8B%E1%85%B5%E1%84%87%E1%85%B3%E1%86%AF%E1%84%8F%E1%85%A1.webp?alt=media&token=e916997b-33b9-4bad-9759-93dada44840a ', '[1일차 - 다리집- 하우스맬- 광안리해변]1. 다리집굵으면서 쫄깃한 떡볶이와, 튀긴 어묵이 오래 기억에 남는다. 떡볶이 양념이 아주 맛있어서 추가 주문을 할 정도였다.2.  하우스맬귀엽고 깔끔한 인테리어인기 메뉴인 소금빵을 먹으려 했으나품절되어 아쉬웠지만,딸기케이크 또한 비치는 광안대교. 너무 좋았다.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T12', '2', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EA%B2%A8%EC%9A%B8%EC%97%AC%ED%96%89%2F12.%20%E1%84%8B%E1%85%A7%E1%84%89%E1%85%AE%20%E1%84%91%E1%85%A9%E1%84%90%E1%85%A9%E1%84%8C%E1%85%A9%E1%86%AB.jpeg?alt=media&token=d1fd9656-95c4-4b9a-b02d-2be9f9b60d94 ', '[2일차 -   AMA(아마)- 우연한 서점- 골목인]1.   AMA(아마)합리적인 가격대와 세련된 인테리어가 매력적인 장소 사장님이 친절하게 손님을 맞이해주신다.상그리아 피쳐가 맛있었다.2.우연한 서점기억에 오래 남을 서점. 찻값 그리고 1,000원만 있으면 분위기 좋은 곳에서책을 읽을수 있다는 장점이 있 방어회 등 여러 회를 포함한 맛있는 음식을 먹을 수 있다.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T13', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EA%B2%A8%EC%9A%B8%EC%97%AC%ED%96%89%2F13.%20%E1%84%83%E1%85%A1%E1%84%85%E1%85%B5%E1%84%8C%E1%85%B5%E1%86%B8.jpg?alt=media&token=b2ee0735-2cd3-4ca4-a744-beab20efaf39 ', '1.독도 전망대오르막길이어서 거동이 불편하신 분들은 도동항 근처 택시정류장에서 택시 탑승 후 일출전망대 표소까지 택시를 탈 수 있다고 합니다 :) (기본 요금 발생)이렇게 안내판을 따라서 올라가면 케이블카를 타는 매표소로 갈 수 있어요.독도박물관도 구경할 수 있는데, 저희는 독도전망 케이블카만 탑승했습니다.인 7500원에 결제 가능하고, 카아서 너무너무너무 맛있어요.비싼 금액이지만 그만큼 투자할 가치가 있어요.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T13', '2', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EA%B2%A8%EC%9A%B8%EC%97%AC%ED%96%89%2F13.%E1%84%80%E1%85%AA%E1%86%BC%E1%84%8B%E1%85%A1%E1%86%AB%E1%84%85%E1%85%B5%E1%84%92%E1%85%A2%E1%84%87%E1%85%A7%E1%86%AB.jpeg?alt=media&token=c0cc0879-c36b-437b-88b9-52f249a352c7 ', '[1일차- 사진공방 소조 - 위생찜닭-월영교]겨울에 다녀오기 좋은짧은 1박2일 안동여행을소개해드릴까 합니다!바로 고고크리스마스 기념으로 1박 2일 안동여행 다녀왔습니다 ㅎㅎㅎ면허 없는 뚜벅이 여행이라외곽에 있는 핫플은 가지 못했고ㅠ힐링이 목적이라완전 널널하게다. 지도를음도는 넘 이 있고코스가 너무 잘되어 있어서 꼭 꼭 다녀오시길 추천드려요! ');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T14', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EA%B2%A8%EC%9A%B8%EC%97%AC%ED%96%89%2F14.%20%E1%84%83%E1%85%A9%E1%86%A8%E1%84%83%E1%85%A9%E1%84%89%E1%85%A2%E1%84%8B%E1%85%AE.jpg?alt=media&token=a4f906c0-6c32-4622-98dc-cbe27e65ca35', '뚜벅이 혼자 1박2일 여수여행을 다녀왔어요""뚜벅이"" ""혼자"" ""1박2일"" 여행이라서교통수단, 동선, 혼밥 가능 여부 등등고려할 요소들이 많이 있었어요특히 1박2일이라는 짧은 여행기간과뚜벅이라는 조건 때문에 동선 짜는 게진짜 중요했고 신경을 많이 썼는데요저와 비슷한 조건인 분들 참고해주세요1. 동서식당냥 쑥떡행히 안겹쳐서 널널하게 썼어요그리고 화장실은 인테리어를 새로 하셨는지완전 현대식이고 괜찮더라구요!');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T14', '2', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EA%B2%A8%EC%9A%B8%EC%97%AC%ED%96%89%2F14.%20%E1%84%83%E1%85%A9%E1%86%A8%E1%84%83%E1%85%A9%E1%84%8C%E1%85%A5%E1%86%AB%E1%84%86%E1%85%A1%E1%86%BC%E1%84%83%E1%85%A3%201.jpg?alt=media&token=e8855bb9-f9d8-40a5-9386-46a009ea88ca ', '[2일차 : 돌산 + 오동도 코스]1. 돌산카페돌산쪽 바다가 예쁘더라고요그래서 더더욱 돌산 오션뷰 카페를 추천합니다저는 블루온으로 갔는데요​체험단으로 다녀온 곳이지만 진짜 좋았어요뷰도 미쳤고 음료나 디저트도 존맛재방문 의사 200%근데 돌산 안쪽에 있어서 뚜벅이는 가기 어려워요버스어서 별로였어요', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T15', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EA%B2%A8%EC%9A%B8%EC%97%AC%ED%96%89%2F15.%E1%84%8B%E1%85%A7%E1%86%BC%E1%84%8B%E1%85%AF%E1%86%AF%E1%84%83%E1%85%A1%E1%86%BC.webp?alt=media&token=1e83bf72-de8f-4aba-9c90-5e3cd73a9aa8 ', '저 오늘 부산 당일치기 여행 다녀왔어요!사실 볼일있어서 간건데일하러간겸에!! 비행기타고 멀리간김에여행 살짝 했어요> <!!1. 광안리해수욕장 스타벅스부산 갈 때마다 꼭 드르는 광안리해수욕장.최근 1년동안 부산을 3번 갔는데3번 다 광안리 해수욕장은 꼭 갔어요!!ㅋㅋㅋ해운대보다 그나마 좀 더 공항에서 가깝고그냥 바다뷰만 있는게 아니라은 기분을 느끼게 된다고하니 주의하세요.....');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T15', '2', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EA%B2%A8%EC%9A%B8%EC%97%AC%ED%96%89%2F15.%E1%84%8B%E1%85%A7%E1%86%BC%E1%84%8B%E1%85%AF%E1%86%AF%E1%84%83%E1%85%A1%E1%86%BC.webp?alt=media&token=1e83bf72-de8f-4aba-9c90-5e3cd73a9aa8 ', '1. 계룡산 국립공원 수통골무려 3시간에 걸쳐서 오게된 계룡산 국립공원!주말이라 사람이 많아서사진마다 사람들이 많이 걸렸다 ㅜㅜ본격적으로 걷기전에입구에 있는 수통골 미로공원에서 잠시 워밍업 했다.미로가 아주 단순하고 짧은 코스라큰 어려움 없이 탈출?할 수 있었다붉게 물드기 시작한 단풍을 보고 있으니 마음이 풍족해지는 기분이 들었다 바람결에 살간은 인으로 갈 때 ktx 이용했어요.혹시나 좌석 잘못 지정하셨을 때는 반환하시고 다시 하시면 됩니다.상단 오른쪽 끝 누르시고 예약 승차권 조회, 취소 / 승차권 반환 버튼 누르시면 돼요.1. 전주 레일바이크옛 전라선인 아중역 폐선부지에 아중역부터 왜망실까지 왕3.4km를 시속 15~20km로 운행할 수 있도록 제작된 철길이라고 해요.엄청 일찍 가서 저희 밖없어서 엄청 빨리 타고 왔는데요.천천히 타시면서 즐기시길ㅋㅋㅋㅋ거의 15분? 만에 왕복한 것 같아요.가격은 2인 25,000원, 3인 29,000원, 4인 33,000원입니다네이버에서 예약하시면 2천~3천 원 할인받으실 수 있어요.2. 전주 한옥마을 한복 대여식사 후에 한복 대여하러 왔습니다.워낙 대여 가격도 비슷하고 하니 그냥 큰 곳 가시면 돼요.저희가 간 곳은 선녀와 나무꾼이라는 곳인데요.가격은 2만 원으로 다른 곳과 거의 통일이에요.한복뿐만 아니라 경성 의상, 교복도 보유하고 있어서 다양하게 입어볼 수 있어요.또한 삼각대, 셀카봉 무료 대여도 해주시고 네이버 리뷰 작성하면 액자와 사진을 인화에서 주세요.요게 아주 마음에 들었쨔나~~~3. 전주 한옥마을 경기전은 이성계의 어진을 봉안하고 제사하는 전각이에요. 왕과의 산책이라는 축제도 하고 있다고 해요.10.28일까지 20:00 - 21:30에 한다고 합니다.야간 행사인 것 같아요.밤에 보면 엄청 예쁘다고 하니 한번 저녁에 방문해 보세요!저는 일정이 있어서 낮에 갔는데 고즈넉하고 관광객도 없어서 너무 좋더라고요.정전은 보물로 지정되어 있어 한번 둘러보셔도 좋아요.보존도 잘 되어 있었고, 한옥 내부에 앉을수도 있고 들여다볼 수 있게끔 되어 있어요.어진 박물관, 솔밭길도 있고~곳곳이 다 포토존이라 한복 입고 방문하면 최고예요.4. 전주 한옥마을 전동성당전주 혼자 당일치기 뚜벅이 여행 코스 가볼 만한 곳으로 추천드리는 전동성당인데요.빨간 벽돌 색감이 너무 예쁘더라고요.');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T16', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EC%9E%90%EC%9C%A0%EC%97%AC%ED%96%89%2F16.%E1%84%8B%E1%85%A7%E1%84%89%E1%85%AE%20%E1%84%8B%E1%85%A9%E1%84%83%E1%85%A9%E1%86%BC%E1%84%83%E1%85%A9.jpeg?alt=media&token=40ec71b4-ae31-453a-bb35-b074ac4ae43c ', '제 버킷 리스트 중에 하나인 우도 비양도 백패킹을 하게 되었습니다.마을버스를 타고 내려서 걸어오면 ~비양도 입구가 보입니다.저도 처음에는 우도 비양도라고 해서우도에서 또 배를 타고 가야 하나 생각했는데백패킹을 하려고 알아보니 다 이어져 있다고 적혀 있어서 ~ 또 하나 배웠습니다. 우도에 차량 입도 방법우도에 숙박을하고 부피를 줄이면 심신이 편하다.');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T16', '2', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EC%9E%90%EC%9C%A0%EC%97%AC%ED%96%89%2F16.%E1%84%8B%E1%85%A7%E1%84%89%E1%85%AE%20%E1%84%8B%E1%85%AD%E1%84%90%E1%85%B3%E1%84%90%E1%85%AE%E1%84%8B%E1%85%A5.jpeg?alt=media&token=a31324a6-6265-483d-b242-19a4acc6d014 ', '이제 봄이 되면백패킹하기 좋은 곳을 소개해드릴까 해요부산에서 가까운가덕도 백패킹하기 좋은 곳을 다녀왔어요열심히 서칭하여 찾아낸 곳인가덕도 갈맷길 5-2구간이예요가덕도는 낚시하러 종종가던 곳새바지항에 주차를 하고 출발했어요주차 무료지만 꽤 많은 낚시인들이 있으니 참고하셔요!오랜만에 갔는데도여전히 가면 밥 먹을 때 편하더라고요.','');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T17', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EC%9E%90%EC%9C%A0%EC%97%AC%ED%96%89%2F17.%E1%84%8B%E1%85%AE%E1%86%AF%E1%84%85%E1%85%B3%E1%86%BC%E1%84%83%E1%85%A9.jpg?alt=media&token=9de2e859-8ffa-405d-89ac-87b2e929c70f ', '장도 예술의 섬 장도는 여수 데이크하기 좋은 곳으로 웅천친수공원에서 장도까지 놓인 진섬다리를 건너서 갈 수 있어요. 연륙교가 놓여 섬 아닌 섬으로 예술 작품들을 곳곳에서 만나볼 수 있어요.장도로 들어서면 갯바위에 설치한 철제 조형물들은 실물인듯 바닷속으로 들어가고 있는 돌게들을 보고 있는 기분이 들었어요. 갈때마다 보게 되에서로나 육체로고 고요한 장면이는 것처럼어떤 경험은 완전히 다른인생을 만든다는 것을,','');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T18', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EC%9E%90%EC%9C%A0%EC%97%AC%ED%96%89%2F18.%20%E1%84%87%E1%85%AE%E1%84%89%E1%85%A1%E1%86%AB%20%E1%84%83%E1%85%A1%E1%86%BC%E1%84%8B%E1%85%B5%E1%86%AF%E1%84%8E%E1%85%B5%E1%84%80%E1%85%B5.jpeg?alt=media&token=2ef29eb8-7c75-4fca-805e-4fb3225579a8 ', '소노캄제주-소노캄제주 하트나무1. 소노캄제주 주차장2. 로비(4층)에서 2층으로 내려가면 야외정원으로 나갈 수 있고, 나가서 바다쪽 야외수영장 오른편 숲속 산책로 쪽에 하트나무 있음3. 킹더랜드라는 드라마에 나와서 더 유명해짐휴애리자연생활농릉관독도박물를 전달하고 있다독도의 역사를 알 수 있어 재미도 있다!','');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T19', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EC%9E%90%EC%9C%A0%EC%97%AC%ED%96%89%2F18.%E1%84%8C%E1%85%A5%E1%86%AB%E1%84%8C%E1%85%AE%E1%84%89%E1%85%A5%E1%86%BC%E1%84%83%E1%85%A1%E1%86%BC.jpg?alt=media&token=2dace468-4e2b-4e21-b1c5-fae423505c92 ', '그런 곳 중에 하나가 바로 장태산 자연휴양림이에요.메타세쿼이아 국내 최대 군락지로 유명하고 요즘 인스에서 사진 멋있게 나오는 핫스팟으로 엄청 유명한 곳이기도 해요.특히 가을에 찾으면 근사한 단풍을 볼 수 있는데 녹음이 짙은 여름에도 근사한 것 같아요~전망대에 올라 굽어 본 풍경이 시원스럽게 펼쳐져요.냉소바 하길있더라기 아쉬워 만두 하나 포장해왔어요.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T20', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EC%9E%90%EC%9C%A0%EC%97%AC%ED%96%89%2F20.%E1%84%8B%E1%85%B2%E1%84%89%E1%85%A5%E1%86%BC%E1%84%80%E1%85%AE%E1%86%A8%E1%84%92%E1%85%AA.jpeg?alt=media&token=9cb5acda-63b9-4290-9dea-9823d50c6979 ', '그런 곳 중에 하나가 바로 장태산 자연휴양림이에요.메타세쿼이아 국내 최대 군락지로 유명하고 요즘 인스에서 사진 멋있게 나오는 핫스팟으로 엄청 유명한 곳이기도 해요.특히 가을에 찾으면 근사한 단풍을 볼 수 있는데 녹음이 짙은 여름에도 근사한 것 같아요~전망대에 올라 굽어 본 풍경이 시원스럽게 펼쳐져 오른면발도 쫄깃하고 국물도 어찌나 시원한지 순식간에 국까지 오기 아쉬워 만두 하나 포장해왔어요.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T21', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EB%B0%B1%ED%8C%A8%ED%82%B9%2F21.%20%E1%84%87%E1%85%A2%E1%86%A8%E1%84%91%E1%85%A2%E1%84%8F%E1%85%B5%E1%86%BC%20%E1%84%80%E1%85%A1%E1%86%BC%E1%84%8B%E1%85%AF%E1%86%AB%E1%84%83%E1%85%A9.jpeg?alt=media&token=cfd33381-9ed2-4cfe-9a7c-a07494ab7650 ', '그런 곳 중에 하나가 바로 장태산 자연휴양림이에요.메타세쿼이아 국내 최대 군락지로 유명하고 요즘 인스에서 사진 멋있게 나오는 핫스팟으로 엄청 유명한 곳이기도 해요.특히 가을에 찾으면 근사한 단풍을 볼 수 있는데 녹음이 짙은 여름에도 근사한 것 같아상는 따뜻한 메밀차가 정말 좋았어요.그냥 오기 아쉬워 만두 하나 포장해왔어요.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T22', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%EA%B5%AD%EB%82%B4-%EB%B0%B1%ED%8C%A8%ED%82%B9%2F22.%E1%84%8B%E1%85%AE%E1%86%AF%E1%84%85%E1%85%B3%E1%86%BC%E1%84%83%E1%85%A9%20%E1%84%87%E1%85%A2%E1%86%A8%E1%84%91%E1%85%A2%E1%84%8F%E1%85%B5%E1%86%BC.jpeg?alt=media&token=67ac0733-b9c8-4217-b8c5-9ae75e2b82ea ', '제주 유명한 연예인들도 방문한 스시 오마카세여행 다녀오면서 10년 째 사랑받고 있는 제주 스시 맛집스시호시카이 방문해서 인생 스시 먹고 와서 소개해드려요.평소 오마카세 가보고 싶었는데 이왕이면 제주에서 유명하고제주시 스시 맛집 제주에서만  상큼코스로 특별한 오마카세 먹을 수 있었습니다.', '');
