INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T40', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%ED%95%B4%EC%99%B8%20-%20%EB%A8%B9%EB%B0%A9%EC%97%AC%ED%96%89%2F2.%20%E1%84%92%E1%85%A2%E1%84%8B%E1%85%AC%20%E1%84%82%E1%85%A2%E1%84%86%E1%85%A9%E1%86%BC%E1%84%80%E1%85%A9%20%E1%84%86%E1%85%A5%E1%86%A8%E1%84%87%E1%85%A1%E1%86%BC.jpeg?alt=media&token=192e172e-09b3-4f40-ab68-1701b1840281', '아침으로는 호텔 조식을 먹는데전날에 야식을 먹었던 터라과일로 간단하게 요기만 했어요​호텔 조식은 즉석요리 코너도 있었고핫푸드,콜드푸드 모두 준비되어 있었어요체크아웃을 하고내몽고 박물관으로 북경에 도착하긴 하더라고요..10었던소, 식사, 교통까지 만족스러웠어요', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T40', '2', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%ED%95%B4%EC%99%B8%20-%20%EB%A8%B9%EB%B0%A9%EC%97%AC%ED%96%89%2F2.%20%E1%84%92%E1%85%A2%E1%84%8B%E1%85%AC%20%E1%84%82%E1%85%A2%E1%84%86%E1%85%A9%E1%86%BC%E1%84%80%E1%85%A9%20%E1%84%86%E1%85%A5%E1%86%A8%E1%84%87%E1%85%A1%E1%86%BC%202%E1%84%8B%E1%85%B5%E1%86%AF%E1%84%8E%E1%85%A1.jpeg?alt=media&token=2e20cc37-8311-4f62-8ed5-c7cd9174dc43','6~7시간의 긴 여정이지만아침 일찍 모인지라 잠자면서 가기도 하고,중간중간 간식도 챙겨주시고휴게소도 들려서지루하지 않게 갈 수 이 싶 휴식을 취하해 주세요!', '');
