INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T58', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%ED%95%B4%EC%99%B8%20-%20%EC%9E%90%EC%9C%A0%EC%97%AC%ED%96%89%2F20.%20%E1%84%90%E1%85%A2%E1%84%80%E1%85%AE%E1%86%A8%20%E1%84%87%E1%85%A1%E1%84%8B%E1%85%AA%E1%84%89%E1%85%B3%E1%84%91%E1%85%A1%201%E1%84%8B%E1%85%B5%E1%86%AF%E1%84%8E%E1%85%A1%20%E1%84%8C%E1%85%A1%E1%84%8B%E1%85%B2.jpeg?alt=media&token=622095f2-fdf0-4e63-9d80-c49635d00fb3 ', '밤베르크 여행 시 가볼 만한 곳으로 막시밀이안광장, 그뤼너 마르크트광장, 구시청사, 작은베네치아, 대성당광장, 구궁전, 신궁전 등이 있는데, 모두 도않아서 뷔르른베르크 가다 창밖으로 본 풍경이 다랍니다~', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T58', '2', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%ED%95%B4%EC%99%B8%20-%20%EC%9E%90%EC%9C%A0%EC%97%AC%ED%96%89%2F20.%20%E1%84%90%E1%85%A2%E1%84%80%E1%85%AE%E1%86%A8%20%E1%84%8F%E1%85%AE%E1%84%8F%E1%85%B5%E1%86%BC%E1%84%8F%E1%85%B3%E1%86%AF%E1%84%85%E1%85%A2%E1%84%89%E1%85%B3%202%E1%84%8B%E1%85%B5%E1%86%AF%E1%84%8E%E1%85%A1%20%E1%84%8C%E1%85%A1%E1%84%8B%E1%85%B2.jpeg?alt=media&token=f5d352ef-c8a6-4405-b588-4bb6c8e9fe12 ', '처음 유럽여행을 계획할 때 보통 서유럽 쪽으로 여행을 계획금 더 건유명한정도 소요가볼오도에 갔었는간다 해랜프랑완간 가격 보셔서 교통 편을 이용하시면 될 것 같아요', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T58', '3', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%ED%95%B4%EC%99%B8%20-%20%EC%9E%90%EC%9C%A0%EC%97%AC%ED%96%89%2F20.%20%E1%84%90%E1%85%A2%E1%84%80%E1%85%AE%E1%86%A8%20%E1%84%8B%E1%85%A1%E1%84%89%E1%85%B5%E1%84%8B%E1%85%A1%E1%84%90%E1%85%B5%E1%84%8F%E1%85%B3%203%E1%84%8B%E1%85%B5%E1%86%AF%E1%84%8E%E1%85%A1%20%E1%84%8C%E1%85%A1%E1%84%8B%E1%85%B2.jpeg?alt=media&token=819aac72-d292-4c0c-9d95-19dc2c4cd657 ', '뉘른베르크 여행 시 가볼 만한 곳은  수공예거리, 성로렌츠성당, 박물관다리, 성모성당, 장난감가 것 같아요. 그래서 돌아보는데도 시간이 많이 걸리지 않았답니다.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T59', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%ED%95%B4%EC%99%B8%20-%EB%B0%B1%ED%8C%A8%ED%82%B9%2F21.%20%E1%84%83%E1%85%A2%E1%84%86%E1%85%A1%E1%86%AB%20%E1%84%87%E1%85%A2%E1%86%A8%E1%84%91%E1%85%A2%E1%84%8F%E1%85%B5%E1%86%BC%201%E1%84%8B%E1%85%B5%E1%86%AF%E1%84%8E%E1%85%A1%20.jpeg?alt=media&token=083af952-a2cf-4113-8f93-74c23d63ce99', '인도에는 수십 종에 달하는 향신료들이 있으며, 혼합된 향신료 가루를 총칭해서 마살(Masala)라고 부르는대요!커리는 밀로 만든전통 빵 ‘난’을 곁들여 먹는 것으로 양고기, 닭고기, 생선을기본 재게 배합된 향신있으면서 가장 아름다운 풍경의 자연을 보여주려고 합니다. 후보니다.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T59', '3', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%ED%95%B4%EC%99%B8%20-%EB%B0%B1%ED%8C%A8%ED%82%B9%2F21.%20%E1%84%83%E1%85%A2%E1%84%86%E1%85%A1%E1%86%AB%20%E1%84%87%E1%85%A2%E1%86%A8%E1%84%91%E1%85%A2%E1%84%8F%E1%85%B5%E1%86%BC%203%E1%84%8B%E1%85%B5%E1%86%AF%E1%84%8E%E1%85%A1.jpeg?alt=media&token=3c143878-954a-493f-8fc7-38f1d8371541 ', '전망대만으로는 아쉽다면, 시드니 타워 레스토랑은 어떨까. 90분 동안 런치 또는 디너를 즐길 수 있는 뷔페로, 전 세계 다채로운 요리를 맛볼 수 있다. 1시간 간격으로 1바퀴 회전하기 때문에, 시드니를 360도로 감상하며 식사할 수 있다.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T60', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%ED%95%B4%EC%99%B8%20-%EB%B0%B1%ED%8C%A8%ED%82%B9%2F22.%20%E1%84%87%E1%85%A6%E1%84%90%E1%85%B3%E1%84%82%E1%85%A1%E1%86%B7%20%E1%84%87%E1%85%A2%E1%86%A8%E1%84%91%E1%85%A2%E1%84%8F%E1%85%B5%E1%86%BC%201%E1%84%8B%E1%85%B5%E1%86%AF%E1%84%8E%E1%85%A1.jpeg?alt=media&token=0e5f0b65-31c7-40dd-b471-3e174be9f794 ', '싱가포르에서 가장 핫한 호텔이라고 해도 과언이 아닐 정도로 전 세계적으로 관심을 받는 곳입니다.21세기 건축의 기적이라 불릴 정도로 세계 건축가들의 경외와 찬가 연결되어하고 있는 2,561개의 객실은 모두국의유에요.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T60', '2', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%ED%95%B4%EC%99%B8%20-%EB%B0%B1%ED%8C%A8%ED%82%B9%2F22.%20%E1%84%87%E1%85%A6%E1%84%90%E1%85%B3%E1%84%82%E1%85%A1%E1%86%B7%202%E1%84%8B%E1%85%B5%E1%86%AF%E1%84%8E%E1%85%A1%20%E1%84%87%E1%85%A2%E1%86%A8%E1%84%91%E1%85%A2%E1%84%8F%E1%85%B5%E1%86%BC.jpeg?alt=media&token=64039480-46d5-427a-9728-92d792c6c0b3 ', '마리나베이샌즈호텔에서 연결된 길로 걸어가면가든스 바이더베이 공원이 나옵니다. 가든스 바이 더 베이는 낮에가면 초록초록 식물의푸르름을 느낄 수 있지만, 저녁에 가면 또 다른꼭 빼놓지 말고챙겨보시길 추천드려요~★', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T60', '3', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%ED%95%B4%EC%99%B8%20-%EB%B0%B1%ED%8C%A8%ED%82%B9%2F22.%20%E1%84%87%E1%85%A6%E1%84%90%E1%85%B3%E1%84%82%E1%85%A1%E1%86%B7%203%E1%84%8B%E1%85%B5%E1%86%AF%E1%84%8E%E1%85%A1%20%E1%84%87%E1%85%A2%E1%86%A8%E1%84%91%E1%85%A2%E1%84%8F%E1%85%B5%E1%86%BC.jpeg?alt=media&token=cfb35d43-1310-4eb5-bce1-24f70d074223 ', '이곳은 싱가포르 필수 인증샷 장소입니다.상체는 육지 동물인 사자, 하체는 바다 동물인 물고기의 모습을 한 머라이언은라는 의미를 가지고 있는데, 머라이언 파크에서 가장 잘 관7뒤쪽으로 새끼 머아 수리을을 수 있습니다.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T61', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%ED%95%B4%EC%99%B8%20-%EB%B0%B1%ED%8C%A8%ED%82%B9%2F23.%20%E1%84%92%E1%85%A9%E1%84%8C%E1%85%AE%20%E1%84%87%E1%85%A2%E1%86%A8%E1%84%91%E1%85%A2%E1%84%8F%E1%85%B5%E1%86%BC%201%E1%84%8B%E1%85%B5%E1%86%AF%E1%84%8E%E1%85%A1.jpeg?alt=media&token=52cf2c17-e046-40c1-966c-235953c44b97', '일본 최북단에 위치한 홋카이도는 일본에서도 인기있는 관광지. 봄부터 여름에 걸쳐 각양 각색의 꽃들이 만발하고, 가을에는 단풍이 아름답고, 겨울에는 새하얀 눈이 대지를 덮고, 사계절 아름다운 고 있다. 홋카 지식과 정보와 함께 소개한다.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T61', '2', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%ED%95%B4%EC%99%B8%20-%EB%B0%B1%ED%8C%A8%ED%82%B9%2F23.%20%E1%84%92%E1%85%A9%E1%84%8C%E1%85%AE%20%E1%84%8B%E1%85%A9%E1%84%87%E1%85%A5%E1%84%85%E1%85%A2%E1%86%AB%E1%84%83%E1%85%B3%20%E1%84%90%E1%85%B3%E1%84%85%E1%85%A2%E1%86%A8%202%E1%84%8B%E1%85%B5%E1%86%AF%E1%84%8E%E1%85%A1%20%E1%84%87%E1%85%A2%E1%86%A8%E1%84%91%E1%85%A2%E1%84%8F%E1%85%B5%E1%86%BC.jpeg?alt=media&token=56081d31-7718-4889-ac63-e0c868670993 ', '또한 해산물과 야채, 과일,  문화와하므로, 크루즈를 즐기는 것도 추천한다.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T61', '3', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%ED%95%B4%EC%99%B8%20-%EB%B0%B1%ED%8C%A8%ED%82%B9%2F23.%20%E1%84%92%E1%85%A9%E1%84%8C%E1%85%AE%20%E1%84%8F%E1%85%B3%E1%84%85%E1%85%A6%E1%84%8B%E1%85%B5%E1%84%90%E1%85%A5%20%E1%84%92%E1%85%A9%E1%84%89%E1%85%AE%20%E1%84%87%E1%85%A2%E1%86%A8%E1%84%91%E1%85%A2%E1%84%8F%E1%85%B5%E1%86%BC%203%E1%84%8B%E1%85%B5%E1%86%AF%E1%84%8E%E1%85%A1.jpeg?alt=media&token=dfaa0596-e908-4a54-a590-6657b46930b0 ', '새하얀 눈으로 덮여 겨울은 다른 계절과는 다른만들어 내고 있다.활동파는 스노우 보드나 스지역도 빼놓서 홋카이도의 대자연을 만끽하자.', '');
INSERT INTO TRAVEL_CONTENT_TB(TRAVEL_NUM,D_DAY,TRAVEL_PIC,TRAVEL_WRITING,TRAVEL_MAP) VALUES('T62', '1', 'https://firebasestorage.googleapis.com/v0/b/wonderwalker-260db.appspot.com/o/%EC%84%A0%EC%98%81%2F%ED%95%B4%EC%99%B8%20-%EB%B0%B1%ED%8C%A8%ED%82%B9%2F24.%20%E1%84%89%E1%85%B5%E1%86%BC%E1%84%80%E1%85%A1%E1%84%91%E1%85%A9%E1%86%AF%201%E1%84%8B%E1%85%B5%E1%86%AF%E1%84%8E%E1%85%A1%20%E1%84%87%E1%85%A2%E1%86%A8%E1%84%91%E1%85%A2%E1%84%8F%E1%85%B5%E1%86%BC.webp?alt=media&token=0cc2ccdf-7761-4070-8fec-3ee7959c4b24 ', '겨울은 중국 여행을 더욱 매력적으로 만드는 시기입니다. 중국에서는 환상적인 눈 풍경과 현지 문화의 축제, 그리고 따뜻한 환대가 여행자들을 기다리고 있 아름다움과 중국의 다채로운 문화를 만나보세요.선전시 유명겨울 여행을 더욱 화려하게 만들어줍니다.', '');
